import "./App.css";
import axios from "axios";
import { useEffect, useState } from "react";

function App() {
	const key = "17574b3f26ea6862ad4242bac500a80d";
	const [search, setSearch] = useState("");

	const [city, setCity] = useState();
	useEffect(() => {
		async function getApi() {
			try {
				const response = await axios.get(
					`https://api.openweathermap.org/data/2.5/weather?q=${search}&appid=${key}&units=metric `
				);
				console.log(response);
				setCity(response.data);
			} catch (error) {
				console.error(error);
			}
		}
		getApi();
	}, []);
	console.log(search);
	return (
		<div className="App">
			<div>
				<div class="mb-3 pt-0">
					<input
						type="text"
						placeholder="Placeholder"
						class="px-3 py-3 placeholder-slate-300 text-slate-600 relative bg-white bg-white rounded text-sm border-0 shadow outline-none focus:outline-none focus:ring w-full"
					/>
				</div>
				<input
					onChange={(e) => setSearch(e.target.value)}
					className="border-8 bg-slate-500"
					type="text"
				/>
			</div>
		</div>
	);
}

export default App;
