import "./App.css";
import axios from "axios";
import { useEffect, useState } from "react";
import city from "./City";

function App() {
	const key = "17574b3f26ea6862ad4242bac500a80d";
	const [search, setSearch] = useState("");

	const [city, setCity] = useState();
	useEffect(() => {
		async function getApi() {
			try {
				const response = await axios.get(
					`https://api.openweathermap.org/data/2.5/weather?q=${search}&appid=${key}&units=metric `
				);
				console.log(response);
				setCity(response.data);
			} catch (error) {
				console.error(error);
			}
		}
		getApi();
	}, [search]);
	console.log(search);
	return (
		<div className="App">
			<div>
				<input
					onChange={(e) => setSearch(e.target.value)}
					type="text"
					placeholder="Placeholder"
					class="px-3 mt-3 mb-3 pt-2.5 w-[250px] py-3 placeholder-slate-300 text-slate-700 relative bg-white bg-dark rounded text-lg border-1 shadow-lg outline-none focus:outline-none focus:ring focus:shadow-xl "
				/>
				<City city={city} />
			</div>
		</div>
	);
}

export default App;
